mapper.select:
SELECT id , firstName , lastName , managerId , managerName , title , department , cellPhone , officePhone , email , city , pic , twitterId , blog , regdate FROM employee WHERE 1=1
AND id = #{id}
AND firstName = #{firstName}

mapper.insert:
		INSERT 
		  INTO employee
		     (
		       id
             , firstName
             , lastName
             , managerId
             , managerName
             , title
             , department
             , cellPhone
             , officePhone
             , email
             , city
             , pic
             , twitterId
             , blog
             , regdate
		     )
		VALUES 
		     (
		       #{id}
             , #{firstName}
             , #{lastName}
             , #{managerId}
             , #{managerName}
             , #{title}
             , #{department}
             , #{cellPhone}
             , #{officePhone}
             , #{email}
             , #{city}
             , #{pic}
             , #{twitterId}
             , #{blog}
		     , now()  
		     )

mapper.update:
		UPDATE 
		  employee
		  set
             firstName = #{firstName}
             , lastName = #{lastName}
             , managerId = #{managerId}
             , managerName = #{managerName}
             , title = #{title}
             , department = #{department}
             , cellPhone = #{cellPhone}
             , officePhone = #{officePhone}
             , email = #{email}
             , city = #{city}
             , pic = #{pic}
             , twitterId = #{twitterId}
             , blog = #{blog}
             , regdate = now()  
		  where id = ${id}
